GroupBox.ClientEvent:
var data = view.get("#dsRolePower").getData("#");
if(data != undefined){
	if(data.get("rolename") == "DeptAdmin"){
		view.get("#deptInfo").set("visible",false);
		view.get("#userInfoName").set("readOnly",false);
		view.get("#conditionForm").get("entity").set({
			deptid:data.get("deptid")
		});
	}else if(data.get("rolename") == "Other"){
		self.set("visible",false);
	}
}

Button.ClientEvent:
var entity = view.get("#conditionForm").get("entity");
var dept = entity.get("dept");
var user = entity.get("user");
var begin = entity.get("begin");
var end = entity.get("end");
if(end < begin && end != null){
	throw new dorado.Exception("终止日期不能小于起始日期！");
}
if(dept == ""){
	view.get("#conditionForm").get("entity").set({
		dept:null,
		deptid:null
		//user:null,
		//userid:null
	});
}
if(user == ""){
	view.get("#conditionForm").get("entity").set({
		user:null,
		userid:null
	});
}else if(entity.get("userid") == ""){
	view.get("#conditionForm").get("entity").set({
		userid:null
	});
}
view.get("#dsYxtk").set("parameter",view.get("#conditionForm").get("entity"));
view.get("#dsYxtk").flushAsync();

ToolBar.ClientEvent:
var data = view.get("#dsRolePower").getData("#");
if(data != undefined){
	if(data.get("rolename") == "Other"){
		view.get("#findbutton").set("visible",false);
		view.get("#inbutton").set("visible",false);
	}
}

Button.ClientEvent:
var entitys = view.get('#tktmGrid.selection');
if(entitys != undefined){
	dorado.MessageBox.confirm("真的要删除当前数据？",function(){
		entitys.each(function(entity){
			entity.remove();
		});
		view.get("#updateAction").execute();
	});
}

var dsYxtk = view.get("#dsYxtk").getData("#");
view.get("#updateyxtkForm").set("readOnly",false);
view.get("#updatetkxzxGrid").set("readOnly",false);
view.get("#submitButton").set("visible",true);
view.get("#tmSelect").set("visible",true);
view.get("#tofselect").set("visible",true);
if(dsYxtk == undefined){
	return;
}
//var content = dsYxtk.get("content");
var mode = dsYxtk.get("mode");

if(mode == 1){
	//content = content + "(单选题)";
	view.get("#updatetkxzxGrid").set("selectionMode","singleRow");
}else if(mode == 2){
	//content = content + "(多选题)";
	view.get("#updatetkxzxGrid").set("selectionMode","multiRows");
}else if(mode == 0){
	//content = content + "(判断题)";
	view.get("#tofxzxGrid").set("selectionMode","singleRow");
}

var entity = view.get("#dsYxtk").getData("#.daxzx");

if(mode ==1 || mode == 2){
	//单选或多选题

	view.get("#updatetofxzxGroup").set("visible",false);
	view.get("#updatetkxzxGroup").set("visible",true);
	view.get("#daxzKey").set("visible",true);
	if(entity.current == undefined){
		view.get("#updatedaxzxGroup").set("visible",false);
		//view.get("#contentLable").set("text",content);
		view.get("#updateyxtkDialog").show();
	}else{
		view.get("#updatedaxzxGroup").set("visible",true);
		var selects = [];
		var xzs = view.get("#dsYxtk").getData("#.tkxzx");
		entity.each(function(da){
			xzs.each(function(xz){
				if(da.get("id") == xz.get("id")){
					selects.push(xz);
				}
			});
		});
		//view.get("#contentLable").set("text",content);
		view.get("#updateyxtkDialog").show();
		if(mode == 1){
			view.get("#updatetkxzxGrid").set("selection",selects[0]);
		}else if(mode == 2){
			view.get("#updatetkxzxGrid").set("selection",selects);
		}
	}
	
}else{
	//判断题
	view.get("#updatetofxzxGroup").set("visible",true);
	view.get("#updatetkxzxGroup").set("visible",false);
	view.get("#daxzKey").set("visible",false);
	
	if(entity.current == undefined){
		view.get("#updatedaxzxGroup").set("visible",false);
	}else{
		var xzs = view.get("#dsYxtk").getData("#.tof");//============
		view.get("#updatedaxzxGroup").set("visible",true);
		var select = null;
		entity.each(function(da){
			xzs.each(function(xz){
				if(da.get("xzKey") == xz.get("xzKey")){
					select = xz;
					da.set({tkId:view.get("#dsYxtk").getData("#").get("id")});
				}
			});
		});
				
	}
	//view.get("#tofcontentLable").set("text",content);	
	view.get("#updateyxtkDialog").show();
	view.get("#tofxzxGrid").set("selection",select);
}

