var entity = view.get("#addNodeForm").get("entity");
//var currentNode = view.get("#datatree").get("currentNode"); 
//var currentEntity = null;
/*
if (currentNode != null) {
	currentEntity = currentNode.get("data");
	
	currentEntity.parent.insert({
		dept_name : entity.get("dept_name")
	}, "begin");
}*/

view.get("#addNodeAction").execute(function(){
	view.get("#addNode").close();
	
	if(entity==undefined || !entity.get("parentId")){
		view.get("#deptDataSet").flush();
	}else{
		var childs = view.get("#datatree").get("currentNode").get("data").get("child");
		childs.each(function(entity){
				
		if(undefined == entity.get("id_")){
			view.get("#deptDataSet").getData("!current_Node.child").flush();
			var newNode = view.get("#deptDataSet").getData("!current_Node.child").getLast();
			entity.set({
				id_:newNode.get("id_"),
				dept_name:newNode.get("dept_name"),
				parentId:newNode.get("parentId"),
				ms:newNode.get("ms")
				});
			}
		});
		
		var newNode = view.get("#deptDataSet").getData("!current_Node.child").getLast();
			view.get("#deptDataSet").getData("!current_Node.childDataGrid").insert({
				id_:newNode.get("id_"),
				dept_name:newNode.get("dept_name"),
				parentId:newNode.get("parentId"),
				ms:newNode.get("ms")
			});
		view.get("#deptDataSet").getData("!current_Node.childDataGrid").flush();//Async();
	}
});

view.get("#addNode").close();
var node = view.get("#deptDataSet").getData("!current_Node");
if(node != undefined){
	view.get("#addNodeDataSet").clear();
	node.remove();
}

var entity = view.get("#addChildForm").get("entity");
/*
