view.get("#addNodeAction").execute(function(){
	view.get("#addChild").close();

	var childs = view.get("#datatree").get("currentNode").get("data").get("child");
	childs.each(function(entity){
		if(undefined == entity.get("id_")){
			view.get("#deptDataSet").getData("!current_Node.child").flush();
			var newNode = view.get("#deptDataSet").getData("!current_Node.child").getLast();
			entity.set({
				id_:newNode.get("id_"),
				dept_name:newNode.get("dept_name"),
				parentId:newNode.get("parentId"),
				ms:newNode.get("ms")
				});
		}
	});
	
	
	var newNode = view.get("#deptDataSet").getData("!current_Node.child").getLast();
	view.get("#deptDataSet").getData("!current_Node.childDataGrid").insert({
			id_:newNode.get("id_"),
			dept_name:newNode.get("dept_name"),
			parentId:newNode.get("parentId"),
			ms:newNode.get("ms")
		});
	view.get("#deptDataSet").getData("!current_Node.childDataGrid").flush();//Async();
});

view.get("#addChild").close();
var child = view.get("#deptDataSet").getData("!current_Node.child");
child.each(function(entity){
	if(undefined == entity.get("id_")){
		entity.remove();
		view.get("#addNodeDataSet").clear();
	}
});

//var parent_Id = view.get("#deptDataSet").getData("!current_Node").get("parentId");
var dept = view.get("#deptDataSet").getData("!current_Node");

if(dept != undefined){
	var parent_Id = dept.get("parentId");
	view.get("#addNodeDataSet").insert({id_:"",dept_name:"",parentId:parent_Id,ms:""});
}else{
	view.get("#addNodeDataSet").insert();
}



var currentNode = view.get("#datatree").get("currentNode"); 
var currentEntity = null;

if (currentNode != null) {
	currentEntity = currentNode.get("data");
	currentEntity.parent.insert({}, "begin");
}

//var currentNode = view.get("#datatree").get("currentNode");
//currentEntity.parent.insert({dept_name:""}, "begin");

view.get("#addNode").show();

var currentEntity = view.get("#datatree").get("currentEntity");
if(!currentEntity){
	throw new dorado.Exception("请先点中一个树节点！");
}
dorado.MessageBox.confirm("真的要删除当前节点数据？",function(){	
	var id = view.get("#deptDataSet").getData("!current_Node").get("id_");
	currentEntity.remove();
	currentEntity.get("child");
	view.get("#deleteAction").set("parameter",id);
	view.get("#deleteAction").execute(function(){
		view.get("#deptDataSet").getData("!current_Node.childDataGrid").flush();//Async();
	});
});

var currentNode = view.get("#datatree").get("currentNode").get("data");

//currentNode.setState(dorado.Entity.STATE_NONE);

var id = view.get("#deptDataSet").getData("!current_Node").get("id_");

view.get("#addNodeDataSet").insert({id_:"",dept_name:"",parentId:id,ms:""});

var currentNode = view.get("#datatree").get("currentNode");
if(!currentNode){
	throw new dorado.Exception("请先点中一个树节点！");
}
var currentEntity = currentNode.get("data");
currentNode.expandAsync(function(){
	currentEntity.createChild("child",{});
});

view.get("#addChild").show();

var dept = view.get("#deptDataSet").getData("!current_Node.childDataGrid").current;
if(dept != undefined){
	dorado.MessageBox.confirm("真的要删除当前子节点数据？",function(){
		var child = view.get("#datatree").get("currentEntity").get("child");
		var id = dept.get("id_");
		child.each(function(entity){
			if(id == entity.get("id_")){
				//alert(dept.get("id_"));
				entity.remove();
				view.get("#deleteAction").set("parameter",id);
				view.get("#deleteAction").execute(function(){
					view.get("#deptDataSet").getData("!current_Node.childDataGrid").flush();//Async();
				});
			}
		})
	});
}
