	entity.each(function(o){
		flid+= o.get("data").get("id")+",";
	})
	var type = condition.get("type");
	if(type == 2){//抢答
		view.get("#dsTktmFilter").set("parameter",{"table":"kstsqd",
		flids:flid});
	}else{
		view.get("#dsTktmFilter").set("parameter",{flids:flid});
	}
	view.get("#dsTktmFilter").flush();
