View.ClientEvent:
self.$openTab = function(config){
    if (!config) {
        return;
    }
    var tabConfig = config.tab;
	
    if (!tabConfig) {
        dorado.MessageBox.alert("必须指定要打开标签的配置");
        return;
    }
    if (!tabConfig || "object" != (typeof tabConfig)) {
        dorado.MessageBox.alert("必须指定打开的Tab页的配置集");
        return;
    }
	/*if(tabConfig.gnmk && tabConfig.gnmk instanceof dorado.Entity){
		tabConfig.gnmk = tabConfig.gnmk.getData();
	}*/
    if (!tabConfig.name && !tabConfig.caption) {
        dorado.MessageBox.alert("必须指定标签的名称");
    }
    if (!tabConfig.caption) {
        tabConfig.caption = tabConfig.name;
    }
    if (!tabConfig.name) {
        tabConfig.name = tabConfig.caption;
    }
    if (!tabConfig.closeable) {
        tabConfig.closeable = true;
    }
	
	var tab = {
		"$type": "Control",
		name: tabConfig.name,
		caption: tabConfig.caption,
		closeable: true,
		"control" : {
		 
				"$type": "IFrame",
				"visible": true,
				"path": $url(tabConfig.path),
				"listener": {
					"onReady": (function(self, arg){
						var cf = 	new CFInstance(
								{ 
								  node:self._doms.iframe,
								   src: $url(tabConfig.path),
								   className:"chromeFrame"
								});
						  
						})
					 
				}

		}

	};
   var removeOld = config.removeOld || "S" == self.$getOpenTabMode();
	var refreshOld = config.refreshOld;
	
	delete(config.removeOld);
	delete(config.refreshOld);
    var index = config.index;
    var tabControlWorkspace = view.get("#tabControlWorkspace");
    
    if ("S" == self.$getOpenTabMode()) {
        tabControlWorkspace.removeAllTabs();
    }
    
    if (!index) {
        index = tabControlWorkspace.get("tabs").size;
    }
	

    var oldTab = tabControlWorkspace.getTab(tabConfig.name);

    if (!oldTab) {
		//modified
	    var currentTab = null;
		
		if( tabConfig.gnmk && (tabConfig.gnmk.openWith) =='0'){
			
				if(window.externalHost){
					//代表用ChromeFrame打开
					currentTab = tabControlWorkspace.addTab(tab, index);
				}else {
					currentTab = tabControlWorkspace.addTab(tabConfig, index);
				}
				
		}else if(tabConfig.gnmk && (tabConfig.gnmk.openWith) =='1'){//主界面一定是使用IE访问，普通打开tab就是用ie
				currentTab = tabControlWorkspace.addTab(tabConfig, index);
		}else{
			
			if(window.externalHost){
				//代表用ChromeFrame打开
				currentTab = tabControlWorkspace.addTab(tab, index);
			}else {
				if(dorado.Browser.msie){
					currentTab = tabControlWorkspace.addTab(tabConfig, index);
				}else{
					currentTab = tabControlWorkspace.addTab(tabConfig, index);
				}
			}
		}
		
        tabControlWorkspace.set("currentTab", currentTab);
   
		

		var iframe_tab = currentTab.get('control').getIFrameWindow();

		
        return currentTab;
    }else{
		//dorado.MessageBox.alert("当前页面已经打开了")	
	}
    if (removeOld) {
        tabControlWorkspace.removeTab(tab);
        tabControlWorkspace.addTab(tabConfig, index);
        return;
    }
    
  
    if (refreshOld) {
        oldTab.set("path", tabConfig.path);
    }
    tabControlWorkspace.set("currentTab", oldTab);
    
};

self.$getOpenTabMode = function(){
    return "M";
}

TabControl.ClientEvent:
var panel = view.get("#panelHeader");
var splitPanel1 = view.get("#splitPanel");

self.addRightToolButton(new dorado.widget.SimpleIconButton(
	{
		icon: "icons/ico_tagMax_h.gif",
		"listener":{
					"onClick":(function(self,arg){
						if(!self.$isCollapse){
							panel.doSetCollapsed(true);
						    splitPanel1.doSetCollapsed(true);
							self.$isCollapse=true;
						}else{
							panel.doSetCollapsed(false);
						    splitPanel1.doSetCollapsed(false);
							self.$isCollapse=false;
						}
						
 
 
					})
				}
	}
));

var menu = view.get("#menuTabControl");
var tab = arg.tab;
menu.getItem("close").set("disabled", tab.get("disabled") || !tab.get("closeable"));

menu.show({
	event: arg.event
});

DataTree.ClientEvent:
var data =view.get("#dataTreeMenu").get("currentNode").get("data")
if(data.get('url')!=null){
	view.$openTab({
            tab: {
                $type: "IFrame",
                path: data.get("url"),
                name: data.get("menu_Name"),
                caption: data.get("menu_Name"),
                closeable: true
          
            }
        });
}

MenuItem.ClientEvent:
var tab = this.id("tabControlWorkspace");
var currentTab = tab.get("currentTab");
 
	tab.removeTab(currentTab);

var tabcontrol = this.id("tabControlWorkspace");
var tab = tabcontrol.get("contextMenuTab");
if (tab) tabcontrol.closeOtherTabs(tab);
