Button.ClientEvent:
var mode = view.get("#dsYxtk").getData("#").get("mode");
var entity = view.get('#yxtkxzxGrid.selection');
if(entity == undefined || entity == ""){
	throw new dorado.Exception("请勾选正确答案！")
	return false;
}

if(entity != undefined && entity != ""){
	view.get("#daGrid").set("visible",true);
	var da = view.get("#dsYxtk").getData("#.daxzx");

	if(da.current == undefined){
	
	
			if(mode == 1){
				da.clear();
				//da.insert({tkId:entity.get("tkId")});
				da.insert({id:entity.get("id"),tkId:entity.get("tkId"),
							xzKey:entity.get("xzKey"),content:entity.get("content")});
			}else if(mode == 2){
				da.clear();
				entity.each(function(e){
					//da.insert({tkId:e.get("tkId")});
					da.insert({id:e.get("id"),tkId:e.get("tkId"),
							xzKey:e.get("xzKey"),content:e.get("content")});
				});
			}
	
	}else{
		if(mode == 1){
			if(da.current.get("id") != entity.get("id")){		//
				da.current.remove();
				da.insert({id:entity.get("id"),tkId:entity.get("tkId"),
						xzKey:entity.get("xzKey"),content:entity.get("content")});
				}
			}else if(mode == 2){
				
				da.each(function(d){
				var i = 0;
				entity.each(function(e){
					if(d.get("id") == e.get("id")){		//
						i++;
					}
				});
				if(i == 0){
					d.remove();
				}
			});
			
			entity.each(function(e){
				var j = 0;
				da.each(function(d){
					if(d.get("id") == e.get("id")){		//
						j++;
					}
				});
				if(j == 0){
					da.insert({id:e.get("id"),tkId:e.get("tkId"),
					xzKey:e.get("xzKey"),content:e.get("content")});
				}
			});
		}

	}
		
}
	
//action.execute
view.get("#updateAction").execute(function(){
	view.get("#dialog").close();
	view.get("#dsYxtk").getData("#.yxtkxzx").flush();
	view.get("#dsYxtk").getData("#.daxzx").flush();
});

var current = view.get("#dsYxtk").getData("#.yxtkxzx").current;
if(current != undefined){
	//daxzx
	dorado.MessageBox.confirm("真的要删除当前数据？",function(){
		var daxzxs = view.get("#dsYxtk").getData("#.daxzx");
		daxzxs.each(function(daxzx){
			if(current.get("xzKey") == daxzx.get("xzKey")){
				daxzx.remove();
			}
		});
		var entity = view.get('#yxtkxzxGrid.selection');
		current.remove();
		if(view.get("#dsYxtk").getData("#").get("mode") == 1){
			if(current != entity){
				view.get("#yxtkxzxGrid").set("selection",entity);
			}
		}
		
	});
}

