var entity = view.get("#conditionForm").get("entity");
var dept = entity.get("dept");
var user = entity.get("user");
var begin = entity.get("begin");
var end = entity.get("end");
if(end < begin && end != null){
	throw new dorado.Exception("起始日期不能小于终止日期！");
}
if(dept == ""){
	view.get("#conditionForm").get("entity").set({
		dept:null,
		deptid:null,
		user:null,
		userid:null
	});
}else if(user == ""){
	view.get("#conditionForm").get("entity").set({
		user:null,
		userid:null
	});
}
view.get("#dsYxtk").set("parameter",view.get("#conditionForm").get("entity"));
view.get("#dsYxtk").flush();

var dsYxtk = view.get("#dsYxtk").getData("#");
if(dsYxtk == undefined){
	return;
}
var content = dsYxtk.get("content");
var mode = dsYxtk.get("mode");

if(mode == 1){
	content = content + "(单选题)";
	view.get("#yxtkxzxGrid").set("selectionMode","singleRow");
}else if(mode == 2){
	content = content + "(多选题)";
	view.get("#yxtkxzxGrid").set("selectionMode","multiRows");
}else if(mode == 0){
	content = content + "(判断题)";
	view.get("#yxtktofGrid").set("selectionMode","singleRow");
}

var entity = view.get("#dsYxtk").getData("#.daxzx");

if(mode ==1 || mode == 2){
	//单选或多选题
	if(entity.current == undefined){
		view.get("#daGrid").set("visible",false);
		view.get("#contentLable").set("text",content);
		view.get("#dialog").show();
	}else{
		view.get("#daGrid").set("visible",true);
		var selects = [];
		var xzs = view.get("#dsYxtk").getData("#.yxtkxzx");
		entity.each(function(da){
			xzs.each(function(xz){
				if(da.get("id") == xz.get("id")){
					selects.push(xz);
				}
			});
		});
		view.get("#contentLable").set("text",content);
		view.get("#dialog").show();
		if(mode == 1){
			view.get("#yxtkxzxGrid").set("selection",selects[0]);
		}else if(mode == 2){
			view.get("#yxtkxzxGrid").set("selection",selects);
		}
	}
	
}else{
	//判断题
	if(entity.current == undefined){
		view.get("#datofGrid").set("visible",false);
	}else{
		var xzs = view.get("#dsYxtk").getData("#.tof");
		view.get("#datofGrid").set("visible",true);
		var select = null;
		entity.each(function(da){
			xzs.each(function(xz){
				if(da.get("xzKey") == xz.get("xzKey")){
					select = xz;
					da.set({tkId:view.get("#dsYxtk").getData("#").get("id")});
				}
			});
		});
				
	}
	view.get("#tofcontentLable").set("text",content);	
	view.get("#tofDialog").show();
	view.get("#yxtktofGrid").set("selection",select);
}
