PropertyDef.ClientEvent:
view.get("#dsZstk").getData("#.tkxzx").cancel();
var yxtk = view.get("#dsZstk").getData("#");
if(yxtk.get("id") == undefined){
	//新增
	if(arg.value == 0){
		view.get("#newxzKey").set("visible",false);
		view.get("#dsZstk").getData("#.tkxzx").insert({id:1,tkId:0,xzKey:"A",content:"对"});
		view.get("#dsZstk").getData("#.tkxzx").insert({id:0,tkId:0,xzKey:"B",content:"错"});
		view.get("#newtkxzxGrid").set("selectionMode","singleRow");
	}else{
		view.get("#newxzKey").set("visible",true);
		view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"A"});
		view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"B"});
		view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"C"});
		view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"D"});
		view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"E"});
		if(arg.value == 1){
			view.get("#newtkxzxGrid").set("selectionMode","singleRow");
		}else if(arg.value == 2){
			view.get("#newtkxzxGrid").set("selectionMode","multiRows");
		}
	}
	view.get("#tkxzxGroup").set("visible",true);
}else{
	if(arg.value == 1){
		view.get("#updatetkxzxGrid").set("selectionMode","singleRow");
	}else if(arg.value == 2){
		view.get("#updatetkxzxGrid").set("selectionMode","multiRows");
	}else if(arg.value == 0){
		view.get("#tofxzxGrid").set("selectionMode","singleRow");
	}
	var das = view.get("#dsZstk").getData("#.daxzx");
	if(das.current != undefined && das.current.get("content") != undefined){
		das.each(function(da){
			da.set({content:null});
		});
	}
	view.get("#updatedaxzxGroup").set("visible",false);
	if(arg.value == 0){
		view.get("#updatetkxzxGroup").set("visible",false);
		view.get("#updatetofxzxGroup").set("visible",true);
	}else{
		view.get("#updatetofxzxGroup").set("visible",false);
		view.get("#updatetkxzxGroup").set("visible",true);
		view.get("#dsZstk").getData("#.tkxzx").cancel();
		view.get("#dsZstk").getData("#.tkxzx").each(function(entity){
			entity.set("content",null);
		});
		if(view.get("#dsZstk").getData("#.tkxzx").current == undefined){
			view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"A",tkId:yxtk.get("id")});
			view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"B",tkId:yxtk.get("id")});
			view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"C",tkId:yxtk.get("id")});
			view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"D",tkId:yxtk.get("id")});
			view.get("#dsZstk").getData("#.tkxzx").insert({xzKey:"E",tkId:yxtk.get("id")});
		}
		
	}
}

var datas = view.get("#dsZstk").getData("#.tkxzx");
if(datas.getLast() != null){
	if(datas.getLast().get("xzKey") == undefined){
		datas.each(function(entity){
			if(entity.get("xzKey") == arg.value){
				//alert(arg.value);
				throw new dorado.Exception("记录已经存在，无法添加！")
				return false;
			}
		});
	}
}

