Button.ClientEvent:
var entity = view.get('#newtkxzxGrid.selection');
var data = view.get("#dsZstk").getData("#");
var mode = data.get("mode");
if(entity != undefined && entity != ""){
	if(data.get("id") == undefined){
		var da = view.get("#dsZstk").getData("#.daxzx");
		if(mode == 1 || mode == 0){
			da.clear();
			da.insert({//id:entity.get("id"),tkId:entity.get("tkId"),
					xzKey:entity.get("xzKey"),content:entity.get("content")});
		}else if(mode == 2){
			da.clear();
			entity.each(function(e){
				da.insert({//id:e.get("id"),tkId:e.get("tkId"),
					xzKey:e.get("xzKey"),content:e.get("content")});
			});
		}
	}
}

view.get("#updateAction").execute(function(){
	view.get("#addyxtkDialog").close();
	view.get("#tkxzxGroup").set("visible",false);
	view.get("#dsZstk").flush();//可能与查询有 冲突
});

var data = view.get("#dsZstk").getData("#");
var mode = data.get("mode");
var da = view.get("#dsZstk").getData("#.daxzx");
if(mode == 0){
	var tofentity = view.get('#tofxzxGrid.selection');
	if(tofentity == undefined || tofentity == ""){
		throw new dorado.Exception("请勾选正确答案！")
		return false;
	}	
	//da.remove();
	da.each(function(d){
		d.remove();
	});
	view.get("#dsZstk").getData("#.tkxzx").each(function(xz){
		xz.remove();
	});
	da.insert({id:tofentity.get("id"),tkId:data.get("id"),
			xzKey:tofentity.get("xzKey"),content:tofentity.get("content")});	
}else if(mode == 1){	
	var entity = view.get('#updatetkxzxGrid.selection');
	if(entity == undefined || entity == ""){
		throw new dorado.Exception("请勾选正确答案！");
		return false;
	}
	//da.clear();
	//da.remove();
	da.each(function(d){
		d.remove();
	});
	da.insert({id:entity.get("id"),tkId:entity.get("tkId"),
			xzKey:entity.get("xzKey"),content:entity.get("content")});
	
	//alert(entity.get("xzKey")+"//"+entity.get("content"));
}else if(mode == 2){
	var entity = view.get('#updatetkxzxGrid.selection');
	if(entity == undefined || entity == ""){
		throw new dorado.Exception("请勾选正确答案！")
		return false;
	}
	//da.clear();
	//da.remove();
	da.each(function(d){
		d.remove();
	});
	entity.each(function(e){
		da.insert({//id:e.get("id"),tkId:e.get("tkId"),
			xzKey:e.get("xzKey"),content:e.get("content")});
	});
}

view.get("#updateAction").execute(function(){
	view.get("#updateyxtkDialog").close();
	view.get("#updatetofxzxGroup").set("visible",true);
	view.get("#dsZstk").flush();//可能与查询有 冲突
});

