GroupBox.ClientEvent:
var data = view.get("#dsUserName").getData("#");
if(data != undefined){
	if(data.get("rolename") == "DeptAdmin"){
		view.get("#deptInfo").set("readOnly",true);
		view.get("#userInfoName").set("readOnly",false);
		view.get("#conditionForm").get("entity").set({
			dept:data.get("deptname"),
			deptid:data.get("deptid")
		});
	}else if(data.get("rolename") == "USER"){
		self.set("visible",false);
	}
}

Button.ClientEvent:
var entity = view.get("#conditionForm").get("entity");
var dept = entity.get("dept");
var user = entity.get("user");
var begin = entity.get("begin");
var end = entity.get("end");
if(end < begin && end != null){
	throw new dorado.Exception("起始日期不能小于终止日期！");
}
if(dept == ""){
	view.get("#conditionForm").get("entity").set({
		dept:null,
		deptid:null
		//user:null,
		//userid:null
	});
}
if(user == ""){
	view.get("#conditionForm").get("entity").set({
		user:null,
		userid:null
	});
}else if(entity.get("userid") == ""){
	view.get("#conditionForm").get("entity").set({
		userid:null
	});
}
view.get("#dsZstk").set("parameter",view.get("#conditionForm").get("entity"));
view.get("#dsZstk").flush();

